import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RecipeForm = ({ onNextStep, formData, setFormData }) => {
  const [name, setName] = useState('');
  const [author, setAuthor] = useState('');
  const [description, setDescription] = useState('');
  const [servings, setServings] = useState('');
  const [caloriesPerServing, setCaloriesPerServing] = useState('');
  const [notes, setNotes] = useState('');
  const [category, setCategory] = useState('');
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/digitalkitchen/form/categories')
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Error retrieving categories');
        }
      })
      .then((data) => {
        setCategories(data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  useEffect(() => {
    if (formData.recipe) {
      const { name, author, description, servings, caloriesPerServing, notes, category } = formData.recipe;
      setName(name || '');
      setAuthor(author || '');
      setDescription(description || '');
      setServings(servings || '');
      setCaloriesPerServing(caloriesPerServing || '');
      setNotes(notes || '');
      setCategory(category || '');
    }
  }, [formData]);

  const handleSubmit = (event) => {
    event.preventDefault();
    const updatedFormData = {
      ...formData,
      recipe: {
        name,
        author,
        description,
        servings,
        caloriesPerServing,
        notes,
        category,
      },
    };
    setFormData(updatedFormData);
    onNextStep();
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input
          type="text"
          value={name}
          onChange={(event) => setName(event.target.value)}
        />
      </label>
      <label>
        Author:
        <input
          type="text"
          value={author}
          onChange={(event) => setAuthor(event.target.value)}
        />
      </label>
      <label>
        Description:
        <textarea
          rows={3}
          value={description}
          onChange={(event) => setDescription(event.target.value)}
        />
      </label>
      <label>
        Servings:
        <input
          type="number"
          value={servings}
          onChange={(event) => setServings(event.target.value)}
        />
      </label>
      <label>
        Calories per serving:
        <input
          type="number"
          value={caloriesPerServing}
          onChange={(event) => setCaloriesPerServing(event.target.value)}
        />
      </label>
      <label>
        Notes:
        <textarea
          value={notes}
          onChange={(event) => setNotes(event.target.value)}
        />
      </label>
      <label>
        Category:
        <select
          value={category}
          onChange={(event) => setCategory(event.target.value)}
        >
          <option value="">-- Select a category --</option>
          {categories.map((category) => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </select>
      </label>
      <button type="submit">Submit</button>
    </form>
  );
};

export default RecipeForm;
