import logo from './logo.svg';
import './App.css';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

function WelcomePage() {
  return (
    <div>
      <h1>Welcome to My App!</h1>
      <p>Thank you for choosing our app. We hope you find it useful.</p>
    </div>
  );
}

const recipeForm = document.querySelector("#recipe-form");
recipeForm.addEventListener("submit", (event) => {



  event.preventDefault();
  const name = document.querySelector("#recipe-name").value;
  const description = document.querySelector("#recipe-description").value;
  const servings = document.querySelector("#recipe-servings").value;
  const caloriesPerServing = document.querySelector("#recipe-calories").value;
  const notes = document.querySelector("#recipe-notes").value;
  const author = document.querySelector("#recipe-author").value;
  const category = document.querySelector("#recipe-category").value;

  const data = {
    name,
    description,
    servings,
    caloriesPerServing,
    notes,
    author,
    category,
  };

  fetch("/recipes", {
    method: "POST",
    body: JSON.stringify(data),
    headers: {
      "Content-Type": "application/json",
    },
  })
    .then((response) => {
      if (response.ok) {
        return response.json();
      } else {
        throw new Error("Error creating recipe.");
      }
    })
    .then((data) => {
      console.log("Recipe created:", data);
    })
    .catch((error) => {
      console.error("Error creating recipe:", error);
    });
});



export default WelcomePage;
