import React from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

class DuplicateRecordAlert extends React.Component {
  render() {
    const { renderRecordResponse } = this.props;

    const onClose = () => {
      window.location.reload();
    }

    return (
      <Modal show={true} onHide={onClose}>
        <Modal.Header closeButton>
          <Modal.Title>ALERT!</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <p>{"Duplicate record found!"}</p>
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary" onClick={onClose}>Close</Button>
          <Button variant="primary" onClick={renderRecordResponse}>{"View Recipe"}</Button>
        </Modal.Footer>
      </Modal>
    );
  }
}

export default DuplicateRecordAlert;
