import React, { useState, useEffect } from "react";
import axios from "axios";

function IngredientForm({ setFormData }) {
  const [name, setName] = useState("");
  const [quantity, setQuantity] = useState("");
  const [measurement, setMeasurement] = useState("");
  const [notes, setNotes] = useState("");
  const [measurements, setMeasurements] = useState([]);

  useEffect(() => {
    // Fetch measurements from the backend
    axios
      .get("http://localhost:8080/digitalkitchen/form/measurements")
      .then((response) => {
        setMeasurements(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleAddIngredient = (e) => {
    e.preventDefault();
    const newIngredient = {
      name,
      quantity,
      measurement,
      notes,
    };

    setFormData((prevFormData) => ({
      ...prevFormData,
      ingredients: [...(prevFormData.ingredients || []), newIngredient],
    }));

    setName("");
    setQuantity("");
    setMeasurement("");
    setNotes("");
  };

  return (
    <div>
      <h2>Add Ingredients</h2>
      <form onSubmit={handleAddIngredient}>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Quantity</th>
              <th>Measurement</th>
              <th>Notes</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </td>
              <td>
                <input
                  type="number"
                  step="0.01"
                  value={quantity}
                  onChange={(e) => setQuantity(e.target.value)}
                  required
                />
              </td>
              <td>
                <select
                  value={measurement}
                  onChange={(e) => setMeasurement(e.target.value)}
                  required
                >
                  <option value="">-- Select a measurement --</option>
                  {measurements.map((measurement) => (
                    <option key={measurement.id} value={measurement.measurement}>
                      {measurement.measurement}
                    </option>
                  ))}
                </select>
              </td>
              <td>
                <input
                  type="text"
                  value={notes}
                  onChange={(e) => setNotes(e.target.value)}
                />
              </td>
            </tr>
          </tbody>
        </table>
        <button type="submit">Add Ingredient</button>
      </form>
    </div>
  );
}

export default IngredientForm;
