import React, { useState } from 'react';
import RecipeForm from './RecipeForm';
import IngredientForm from './IngredientForm';
import StepsForm from './StepsForm';
import TagsForm from './TagsForm';

const MultiStepForm = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({});

  const handleNextStep = () => {
    setStep(step + 1);
  };

  const handlePrevStep = () => {
    setStep(step - 1);
  };

  const renderFormStep = () => {
    switch (step) {
      case 1:
        return <RecipeForm formData={formData} setFormData={setFormData} onNextStep={handleNextStep} />;
      case 2:
        return <IngredientForm formData={formData} setFormData={setFormData} onNextStep={handleNextStep} onPrevStep={handlePrevStep} />;
      case 3:
        return <StepsForm formData={formData} setFormData={setFormData} onNextStep={handleNextStep} onPrevStep={handlePrevStep} />;
      case 4:
        return <TagsForm formData={formData} setFormData={setFormData} onPrevStep={handlePrevStep} />;
      default:
        return null;
    }
  };

  return (
    <div>
      {renderFormStep()}
    </div>
  );
};

export default MultiStepForm;
