import React from 'react';
import { Button } from 'react-bootstrap';
import DisplayPage from './pages/DisplayPage';

class SearchResultsDisplay extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentRecipeIndex: props.currentRecipeIndex,
      recipes: props.recipes,
    };
  }

  componentDidUpdate(prevProps) {
    if (prevProps.currentRecipeIndex !== this.props.currentRecipeIndex) {
      this.setState({ currentRecipeIndex: this.props.currentRecipeIndex });
    }
  }

  handlePrevClick = () => {
    this.currentRecipeIndex !== 0 && this.setState((prevState) => ({
      currentRecipeIndex: prevState.currentRecipeIndex - 1,
    }));
  };

  handleNextClick = () => {
    this.currentRecipeIndex !== 0 && this.setState((prevState) => ({
      currentRecipeIndex: prevState.currentRecipeIndex + 1,
    }));
  };

  render() {
    const { currentRecipeID, recipes } = this.state;

    return (
      <div key={currentRecipeID}>
        <Button onClick={this.handlePrevClick}>{'<'}</Button>
        <DisplayPage data={recipes[currentRecipeID]} />
        <Button onClick={this.handleNextClick}>{'>'}</Button>
      </div>
    );
  }
}

export default SearchResultsDisplay;
