import React from 'react';
import { useNavigate } from 'react-router-dom';
import MultiStepForm from './forms/MultiStepForm';
import RecipeDisplay from './RecipeDisplayPage';
import Recipe from './Recipe';
import DisplayRecordPage from './DisplayRecordPage';
//import '../styles/CreateRecipePage.css'

function CreateRecipePage() {
  const navigate = useNavigate();
  let recordDisplay = false;
  const goHome = () => {
    navigate('/');
  };

  const displayRecord = (record) => {
    recordDisplay = true;
    renderDisplay(record);
  }

  const renderDisplay = (record) => {
    console.log(recordDisplay);
    console.log(record);
    return (
    <div>
      {recordDisplay ? (
        <DisplayRecordPage record={record}/>
      ) : (
        <MultiStepForm displayThisRecord={displayRecord} />
      )}
    </div>
    )
  }

  return (
    <div>
      <button onClick={goHome} style={{ position: 'absolute', top: 0, left: 0 }}>
        Home
      </button>
      <h1>Create A Recipe</h1>
      <MultiStepForm displayThisRecord={displayRecord} />
    </div>
  );
}

export default CreateRecipePage;
