import React, { useState, useEffect } from 'react';
import '../../styles/RecipeForm.css';

const RecipeForm = ({ onNextStep, formData, setFormData }) => {
  const [name, setName] = useState(formData && formData.name ? formData.name : '');

  const [author, setAuthor] = useState(formData.author || '');
  const [description, setDescription] = useState(formData.description || '');
  const [servings, setServings] = useState(formData.servings || '');
  const [caloriesPerServing, setCaloriesPerServing] = useState(formData.caloriesPerServing || '');
  const [notes, setNotes] = useState(formData.notes || '');
  const [category, setCategory] = useState(formData.category || '');
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/digitalkitchen/form/categories')
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Error retrieving categories');
        }
      })
      .then((data) => {
        setCategories(data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    const updatedFormData = {
      ...formData,
      name,
      description,
      servings,
      caloriesPerServing,
      notes,
      category,
      author,
    };
    setFormData(updatedFormData);
    onNextStep(); // Navigate to the next step
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input
          className="name"
          type="text"
          value={name}
          onChange={(event) => setName(event.target.value)}
        />
      </label>
      <label>
        Author:
        <input
          className="author"
          type="text"
          value={author}
          onChange={(event) => setAuthor(event.target.value)}
        />
      </label>
      <label>
        Description:
        <textarea
          className="description"
          rows={3}
          value={description}
          onChange={(event) => setDescription(event.target.value)}
        />
      </label>
      <label>
        Servings:
        <input
          className="servings"
          type="number"
          value={servings}
          onChange={(event) => setServings(event.target.value)}
        />
      </label>
      <label>
        Calories per serving:
        <input
          className="calories"
          type="number"
          value={caloriesPerServing}
          onChange={(event) => setCaloriesPerServing(event.target.value)}
        />
      </label>
      <label>
        Notes:
        <textarea
          className="notes"
          value={notes}
          onChange={(event) => setNotes(event.target.value)}
        />
      </label>
      <label>
        Category:
        <select
          className="category"
          value={category}
          onChange={(event) => setCategory(event.target.value)}
        >
          <option value="">--Select a category--</option>
          {categories.map((category) => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </select>
      </label>
      <button type="submit">Submit</button>
    </form>
  );
};

export default RecipeForm;
