import React, { useState } from 'react';

const SearchPage = () => {
  const [searchType, setSearchType] = useState('recipes');
  const [name, setName] = useState('');
  const [ingredients, setIngredients] = useState([]);
  const [authors, setAuthors] = useState([]);
  const [categories, setCategories] = useState([]);
  const [minServings, setMinServings] = useState('');
  const [maxServings, setMaxServings] = useState('');
  const [minCalories, setMinCalories] = useState('');
  const [maxCalories, setMaxCalories] = useState('');
  const [tags, setTags] = useState([]);

  const handleSearchTypeChange = (event) => {
    setSearchType(event.target.value);
  };

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleIngredientInputChange = (event) => {
    // Function call to get ingredient names based on user input
    // Implement the function to fetch ingredient names from the backend
    // and set the suggestions in the component state
    // For example:
    // fetchIngredientNames(event.target.value)
    //   .then((response) => {
    //     setIngredientSuggestions(response.data);
    //   })
    //   .catch((error) => {
    //     console.error('Error fetching ingredient names:', error);
    //   });
  };

  const handleIngredientAdd = (event) => {
    if (event.key === 'Enter' || event.key === ',') {
      const newIngredient = event.target.value.trim();
      if (newIngredient !== '' && !ingredients.includes(newIngredient)) {
        setIngredients([...ingredients, newIngredient]);
        event.target.value = '';
      }
    }
  };

  const handleAuthorAdd = (event) => {
    const newAuthor = event.target.value.trim();
    if (newAuthor !== '' && !authors.includes(newAuthor)) {
      setAuthors([...authors, newAuthor]);
      event.target.value = '';
    }
  };

  const handleCategoryAdd = (event) => {
    const newCategory = event.target.value.trim();
    if (newCategory !== '' && !categories.includes(newCategory)) {
      setCategories([...categories, newCategory]);
      event.target.value = '';
    }
  };

  const handleTagAdd = (event) => {
    const newTag = event.target.value.trim();
    if (newTag !== '' && !tags.includes(newTag)) {
      setTags([...tags, newTag]);
      event.target.value = '';
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Prepare the search criteria JSON object
    const searchCriteria = {
      name: name,
      authors: authors,
      categories: categories,
      minServings: minServings,
      maxServings: maxServings,
      minCalories: minCalories,
      maxCalories: maxCalories,
      ingredients: ingredients,
      tags: tags,
    };

    // Submit the search criteria to the backend
    // Implement the function to send the search criteria JSON object to the backend
    // For example:
    // submitSearchCriteria(searchCriteria)
    //   .then((response) => {
    //     // Process the search results returned by the backend
    //   })
    //   .catch((error) => {
    //     console.error('Error submitting search criteria:', error);
    //   });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <select value={searchType} onChange={handleSearchTypeChange}>
            <option value="recipes">Recipes</option>
            <option value="ingredients">Ingredients</option>
          </select>
          {searchType === 'ingredients' && (
            <input
              type="text"
              placeholder="Enter ingredients"
              onChange={handleIngredientInputChange}
              onKeyUp={handleIngredientAdd}
            />
          )}
          {searchType === 'recipes' && (
            <input
              type="text"
              placeholder="Enter recipe name"
              onChange={handleNameChange}
            />
          )}
          <button type="submit">Search</button>
        </div>

        
        <div>
          {/* Filtering options */}
          {/* Add input fields for authors, categories, servings, and calories */}
          {/* Implement the corresponding add functions for each option */}
        </div>
      </form>
    </div>
  );
};

export default SearchPage;
