import React, { useState } from 'react';
import RecipeForm from './RecipeForm';
import IngredientForm from './IngredientForm';
import StepsForm from './StepsForm';
import TagsForm from './TagsForm';
import '../../styles/MultiStepForm.css'; 

const MultiStepForm = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({ tags: [] });
  const [isNextButtonDisabled, setIsNextButtonDisabled] = useState(true);

  const handleNextStep = (data) => {
    setFormData(formData);
    setStep((prevStep) => prevStep + 1);
  };
  
  const handlePrevStep = () => {
    setStep((prevStep) => prevStep - 1);
  };

  const handleMasterSubmit = () => {
    console.log('Submitted Data:', formData);
    // Make an HTTP request to send the form data to the backend
    // ...
  };

  const handleInputChange = (field, value) => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      recipe: {
        ...prevFormData.recipe,
        [field]: value
      }
    }));
  };

  const handleFormCompletion = (isComplete) => {
    setIsNextButtonDisabled(!isComplete);
  };

  const isRequiredFieldsFilled = () => {
    const { category, name, author } = formData.recipe;
    return category !== '' && name !== '' && author !== '';
  };

  const renderFormStep = () => {
    switch (step) {
      case 1:
        return (
          <RecipeForm 
            onNextStep={handleNextStep} 
            formData={formData} 
            onInputChange={handleInputChange}
            onFormCompletion={handleFormCompletion}
          />
        );
      case 2:
        return (
          <IngredientForm
            onNextStep={handleNextStep}
            onPrevStep={handlePrevStep}
            formData={formData}
            onFormCompletion={handleFormCompletion}
          />
        );
      case 3:
        return (
          <StepsForm
            onNextStep={handleNextStep}
            onPrevStep={handlePrevStep}
            formData={formData}
            onFormCompletion={handleFormCompletion}
          />
        );
      case 4:
        return (
          <TagsForm
            onPrevStep={handlePrevStep}
            formData={formData}
            onFormCompletion={handleFormCompletion}
          />
        );
      default:
        return null;
    }
  };

  return (
    <div>
      {renderFormStep()}
      <div>
        {step > 1 && (
          <button onClick={handlePrevStep}>Previous</button>
        )}
        {step < 4 ? (
          <button
            onClick={() => handleNextStep({})}
            disabled={isNextButtonDisabled}
          >
            Next
          </button>
        ) : (
          <button onClick={handleMasterSubmit}>Submit</button>
        )}
      </div>
    </div>
  );
};

export default MultiStepForm;
