import React, { useState } from 'react';
import { Button, Form } from 'react-bootstrap';

const SearchBar = () => {
  const [name, setName] = useState('');
  const [ingredients, setIngredients] = useState([]);
  const [authors, setAuthors] = useState([]);
  const [categories, setCategories] = useState([]);
  const [servings, setServings] = useState('');
  const [calories, setCalories] = useState('');
  const [tags, setTags] = useState([]);
  const [isFiltering, setIsFiltering] = useState(false);
  const [categorySuggestions, setCategorySuggestions] = useState([]);
  const [authorSuggestions, setAuthorSuggestions] = useState([]);
  const [ingredientSuggestions, setIngredientSuggestions] = useState([]);

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  // FILTERS

  const handleInputKeyDown = (event) => {
    if (event.key === 'Enter' || event.key === ',') {
      event.preventDefault();
      const trimmedValue = event.target.value.trim();
  
      if (trimmedValue !== '' && event.target.name === 'tags') {
        setState((prevState) => [...prevIngredients, trimmedValue]);
      }
  
      event.target.value = ''; // Clear the input value after adding the tag
    }
  };
  
  

  const handleIngredientAdd = (event) => {
    if (event.key === 'Enter' || event.key === ',') {
      const newIngredient = event.target.value.trim();
      if (newIngredient !== '' && !ingredients.includes(newIngredient)) {
        setIngredients([...ingredients, newIngredient]);
        event.target.value = '';
      }
    }
  };

  const handleAuthorAdd = (event) => {
    const newAuthor = event.target.value.trim();
    if (newAuthor !== '' && !authors.includes(newAuthor)) {
      setAuthors([...authors, newAuthor]);
      event.target.value = '';
    }
  };

  const handleCategoryAdd = (event) => {
    const newCategory = event.target.value.trim();
    if (newCategory !== '' && !categories.includes(newCategory)) {
      setCategories([...categories, newCategory]);
      event.target.value = '';
    }
  };

  const handleServingsAdd = (event) => {
    setServings(event.target.value);
  }

  const handleCaloriesAdd = (event) => {
    setCalories(event.target.value);
  }

  const handleTagAdd = (event) => {
    const newTag = event.target.value.trim();
    if (newTag !== '' && !tags.includes(newTag)) {
      setTags([...tags, newTag]);
      event.target.value = '';
    }
  }

  const handleRemoveTag = (index) => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      tags: prevFormData.tags.filter((_, i) => i !== index),
    }));
  }

  const handleFilter = (event) => {
    setIsFiltering(!isFiltering);
  }

  const removeFilters = (event) => {
    setIngredients = useState([]);
    setAuthors = useState([]);
    setCategories = useState([]);
    setMinServings = useState('');
    setMaxServings = useState('');
    setMinCalories = useState('');
    setMaxCalories = useState('');
    setTags = useState([]);
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log(servings);

    // Prepare the search criteria JSON object
    const searchCriteria = {
      name: name,
      authors: authors,
      categories: categories,
      servings: servings,
      calories: calories,
      ingredients: ingredients,
      tags: tags,
    };

    console.log("Searching for: ",searchCriteria);

    try {
        const response = await fetch('http://localhost:8080/digitalkitchen/search', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(searchCriteria),
        });
    
    } catch (error) {

    }
  };

  return (
    <div>
      <Form onSubmit={handleSubmit}>
      <Form.Group className="name">
            <Form.Control
              type="text"
              placeholder="Enter recipe name"
              onChange={handleNameChange}
            />
          <Button type="button" onClick={handleFilter}>Filter</Button>
          <Button type="submit">Search</Button>
        </Form.Group>

        {isFiltering && (
        <div >
          <Form.Group className="ingredients">
            <label>Ingredients:</label>
            <input type="text" class="form-control" placeholder="Enter ingredients..."/>
          </Form.Group >

          <Form.Group className="authors">
            <label>Authors:</label>
            <Button class="form-control">--select authors--</Button>
          </Form.Group>

          <Form.Group className="categories">
            <label>Categories:</label>
            <Button class="form-control">
                <option value="">--select categories--</option>
                    {categories.map((category) => (
                <option key={category.id} value={category.id}>
                    {category.name}
                </option>
          ))}
            </Button>
          </Form.Group>

          <Form.Group className="servings">
            <label>Servings:</label>
            <Form.Control
             type="number" 
             class="form-control" 
             placeholder=""
             onChange={handleServingsAdd}
             />
          </Form.Group>

          <Form.Group className="calories">
            <label>Calories:</label>
            <Form.Control 
            type="number" 
            class="form-control" 
            placeholder=""
            onChange={handleCaloriesAdd}
            />
          </Form.Group>

          <Form.Group className="tags">
            <label>Tags</label>
            <Form.Control 
            type="text" 
            class="form-control" 
            placeholder="Press enter after each"
            onKeyDown={(event) => 
            handleInputKeyDown
                (event, 'tags', tags, setTags)}
            />
          </Form.Group>

          <div>
          { tags.length > 0 ? (
          tags.map((tag, index) => (
            <span key={index} className="tag">
              {<Button href="#" className='tags'/>}{tag}
              <Button variant="danger" size="sm" onClick={() => handleRemoveTag(index)}>
                x
              </Button>
            </span>
          ))
        ) : (
          <p>No tags added yet.</p>
        )}
      </div>


          <Button onClick={removeFilters}>Remove</Button>
        </div>
        )}


      </Form>
    </div>
  );
};

export default SearchBar;
