import React from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

class DuplicateRecordAlert extends React.Component {
  render() {
    const { renderRecordResponse } = this.props;

    const onClose = () => {
      window.location.reload();
    }

    return (
      <Modal show={true} onHide={onClose}>
        <div style={modalStyles}>
          <h3>Duplicate Record Alert</h3>
          <p>Duplicate record found!</p>
          <div>
            <Button variant="secondary" style={buttonStyles} onClick={onClose}>
              Close
            </Button>
            <Button variant="primary" onClick={renderRecordResponse}>
              View Recipe
            </Button>
          </div>
        </div>
      </Modal>
    );
  }
}

export default DuplicateRecordAlert;
