import React, { useState, useEffect } from 'react';
import '../../styles/RecipeForm.css';

const RecipeForm = ({ onNextStep, formData, setFormData }) => {
  const [name, setName] = useState(formData?.recipe?.name || '');
  const [author, setAuthor] = useState(formData?.recipe?.author || '');
  const [description, setDescription] = useState(formData?.recipe?.description || '');
  const [servings, setServings] = useState(formData?.recipe?.servings || '');
  const [caloriesPerServing, setCaloriesPerServing] = useState(formData?.recipe?.caloriesPerServing || '');
  const [notes, setNotes] = useState(formData?.recipe?.notes || '');
  const [category, setCategory] = useState(formData?.recipe?.category || '');
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/digitalkitchen/form/categories')
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Error retrieving categories');
        }
      })
      .then((data) => {
        setCategories(data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  useEffect(() => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      recipe : {
        name,
        description,
        servings,
        caloriesPerServing,
        notes,
        category,
        author,
      },
    }));
  }, [name, description, servings, caloriesPerServing, notes, category, author, setFormData]);

  const handleSubmit = (event) => {
    event.preventDefault();
    onNextStep(); // Navigate to the next step
  };


  const checkReq = () => {
    if (formData.recipe.name != '' & formData.categories.category != '' & formData.categories.author != '')
      return true;
    else
      return false;
  }

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input
          className="name"
          type="text"
          value={name}
          onChange={(event) => { 
            setName(event.target.value); 
            checkReq();}}
          maxLength={50}
          required
        />
      </label>
      <label>
        Author:
        <input
          className="author"
          type="text"
          value={author}
          onChange={(event) => {
            setAuthor(event.target.value);
            checkReq();}}
          maxLength={25}
          required
        />
      </label>
      <label>
        Description:
        <textarea
          className="description"
          rows={3}
          value={description}
          onChange={(event) => setDescription(event.target.value)}
          maxLength={500}
        />
      </label>
      <label>
        Servings:
        <input
          className="servings"
          type="number"
          value={servings}
          onChange={(event) => setServings(event.target.value)}
          min={0}
          step={1}
          pattern="[0-9]*"
          inputMode="numeric"
        />
      </label>
      <label>
        Calories per serving:
        <input
          className="calories"
          type="number"
          value={caloriesPerServing}
          onChange={(event) => setCaloriesPerServing(event.target.value)}
          min={0}
          step={1}
          pattern="[0-9]*"
          inputMode="numeric"
        />
      </label>
      <label>
        Notes:
        <textarea
          className="notes"
          value={notes}
          onChange={(event) => setNotes(event.target.value)}
          maxLength={200}
        />
      </label>
      <label>
        Category:
        <select
          className="category"
          value={category}
          onChange={(event) => {
            setCategory(event.target.value); 
            checkReq();}}
          required
        >
          <option value="">--Select a category--</option>
          {categories.map((category) => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </select>
      </label>
    </form>
  );
};

export default RecipeForm;
