const RecipeForm = ({ formData, setFormData }) => {
  const [name, setName] = useState(formData?.recipe?.name || '');
  const [author, setAuthor] = useState(formData?.recipe?.author || '');
  const [description, setDescription] = useState(formData?.recipe?.description || '');
  const [servings, setServings] = useState(formData?.recipe?.servings || '');
  const [caloriesPerServing, setCaloriesPerServing] = useState(formData?.recipe?.caloriesPerServing || '');
  const [notes, setNotes] = useState(formData?.recipe?.notes || '');
  const [category, setCategory] = useState(formData?.recipe?.category || '');
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/digitalkitchen/form/categories')
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Error retrieving categories');
        }
      })
      .then((data) => {
        setCategories(data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  useEffect(() => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      recipe: {
        ...prevFormData.recipe,
        name,
        author,
        description,
        servings,
        caloriesPerServing,
        notes,
        category,
      },
    }));
  }, [name, author, description, servings, caloriesPerServing, notes, category, setFormData]);


  return (
    <form onSubmit={handleNextStep}>
      <label>
        Name:
        <input
          className="name"
          type="text"
          value={name}
          onChange={(event) => setName(event.target.value)}
        />
      </label>
      <label>
        Author:
        <input
          className="author"
          type="text"
          value={author}
          onChange={(event) => setAuthor(event.target.value)}
        />
      </label>
      <label>
        Description:
        <textarea
          className="description"
          rows={3}
          value={description}
          onChange={(event) => setDescription(event.target.value)}
        />
      </label>
      <label>
        Servings:
        <input
          className="servings"
          type="number"
          value={servings}
          onChange={(event) => setServings(event.target.value)}
        />
      </label>
      <label>
        Calories per serving:
        <input
          className="calories"
          type="number"
          value={caloriesPerServing}
          onChange={(event) => setCaloriesPerServing(event.target.value)}
        />
      </label>
      <label>
        Notes:
        <textarea
          className="notes"
          value={notes}
          onChange={(event) => setNotes(event.target.value)}
        />
      </label>
      <label>
        Category:
        <select
          className="category"
          value={category}
          onChange={(event) => setCategory(event.target.value)}
        >
          <option value="">--Select a category--</option>
          {categories.map((category) => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </select>
      </label>
    </form>
  );
};

export default RecipeForm;
