import React from 'react';
import { Button } from 'react-bootstrap';
import DisplayPage from './pages/DisplayPage';

const SearchResultsDisplay = ({ currentRecipeID, recipes }) => {
  const handlePrevClick = () => {
    if (currentRecipeID > 0) {
      // Decrease the currentRecipeID by 1 if it's greater than 0
      setCurrentRecipeID(currentRecipeID - 1);
    }
  };

  const handleNextClick = () => {
    if (currentRecipeID < recipes.length - 1) {
      // Increase the currentRecipeID by 1 if it's less than the last index
      setCurrentRecipeID(currentRecipeID + 1);
    }
  };

  return (
    <div>
      <Button onClick={handlePrevClick}>{'<'}</Button>
      <DisplayPage data={recipes[currentRecipeID]} />
      <Button onClick={handleNextClick}>{'>'}</Button>
    </div>
  );
};

export default SearchResultsDisplay;
