import Table from 'react-bootstrap/Table';
import React, { useState, useEffect } from 'react';
import DisplayPage from './pages/DisplayPage';

const SearchResults = ({ results, switchDisplayView }) => {
  const [recipes, setRecipes] = useState([]);
  const [useTableView, setUseTableView] = useState([true]);
  const [currentRecipe, setCurrentRecipe] = useState([]);

  useEffect(() => {
    setRecipes(results);
  }, [results]);

  const handleRowClick = (recipe) => {
    console.log("click");
    setCurrentRecipe(recipe);
    setUseTableView(false);
  }

  const renderDisplay = () => {
    return (
    <SearchResultsDisplay  setRecipes={recipes} CurrentRecipe={currentRecipe}/>
    )
  }

  return (
    <>
      {recipes.length > 0 && useTableView && (
        <Table striped bordered hover variant="dark">
          <thead>
            <tr>
              <th>Recipe</th>
              <th>Author</th>
              <th>Category</th>
            </tr>
          </thead>
          <tbody>
            {recipes.map((recipe) => (
              <tr key={recipe.id} onClick={() => handleRowClick(recipe)}>
                <td>{recipe.name}</td>
                <td>{recipe.author}</td>
                <td>{recipe.category}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
      {recipes.length > 0 && !useTableView && renderDisplay(
      
      )}
    </>
  );
};

export default SearchResults;
