import React, { useState, useEffect } from "react";
import axios from "axios";

function IngredientForm({ onNextStep, onPrevStep }) {
  const [ingredients, setIngredients] = useState([]);
  const [name, setName] = useState("");
  const [quantity, setQuantity] = useState("");
  const [measurement, setMeasurement] = useState("");
  const [notes, setNotes] = useState("");
  const [measurements, setMeasurements] = useState([]);

  useEffect(() => {
    // Fetch measurements from the backend
    axios
      .get("http://localhost:8080/digitalkitchen/measurements")
      .then((response) => {
        setMeasurements(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const newIngredient = {
      name,
      quantity,
      measurement,
      notes,
    };
    setIngredients([...ingredients, newIngredient]);
    setName("");
    setQuantity("");
    setMeasurement("");
    setNotes("");
  };

  const handleNext = () => {
    onNextStep();
  };

  const handlePrev = () => {
    onPrevStep();
  };

  return (
    <div>
      <h2>Add Ingredients</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        </div>
        <div>
          <label>Quantity:</label>
          <input type="number" step="0.01" value={quantity} onChange={(e) => setQuantity(e.target.value)} required />
        </div>
        <div>
          <label>Measurement:</label>
          <select value={measurement} onChange={(e) => setMeasurement(e.target.value)} required>
            <option value="">-- Select a measurement --</option>
            {measurements.map((measurement) => (
              <option key={measurement.id} value={measurement.id}>
                {measurement.name}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Notes:</label>
          <textarea value={notes} onChange={(e) => setNotes(e.target.value)} />
        </div>
        <button type="submit">Add Ingredient</button>
      </form>
      <div>
        <h3>Ingredients:</h3>
        <ul>
          {ingredients.map((ingredient, index) => (
            <li key={index}>
              {ingredient.quantity} {ingredient.measurement} {ingredient.name} - {ingredient.notes}
            </li>
          ))}
        </ul>
      </div>
      <button onClick={handlePrev}>Previous Step</button>
      <button onClick={handleNext}>Next Step</button>
    </div>
  );
}

export default IngredientForm;
