import React, { Component } from 'react';
import { Form, Button } from 'react-bootstrap';

class RecipeDisplay extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isEditing: false,
      formData: { ...props.formData },
    };
  }

  handleEdit = () => {
    this.setState({ isEditing: true });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { formData } = this.state;
    // Perform form submission to update the recipe details
    // You can use the formData object to send the updated data to the backend
    console.log(formData);
    // Reset the editing state
    this.setState({ isEditing: false });
  };

  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState((prevState) => ({
      formData: {
        ...prevState.formData,
        [name]: value,
      },
    }));
  };

  render() {
    const { isEditing, formData } = this.state;

    return (
      <div>
        <h2>Recipe Details</h2>
        {isEditing ? (
          <Form onSubmit={this.handleSubmit}>
            {/* Existing form fields */}
            {/* ... */}

            {/* New form fields */}
            <Form.Group controlId="recipeCategory">
              <Form.Label>Category</Form.Label>
              <Form.Control
                type="text"
                name="category"
                value={formData.category}
                onChange={this.handleChange}
              />
            </Form.Group>

            <Form.Group controlId="recipeIngredients">
              <Form.Label>Ingredients</Form.Label>
              <Form.Control
                as="textarea"
                name="ingredients"
                value={formData.ingredients.map((ingredient) => JSON.stringify(ingredient))}
                onChange={this.handleChange}
              />
            </Form.Group>

            <Form.Group controlId="recipeTags">
              <Form.Label>Tags</Form.Label>
              <Form.Control
                type="text"
                name="tags"
                value={formData.tags.join(', ')}
                onChange={this.handleChange}
              />
            </Form.Group>

            <Form.Group controlId="recipeSteps">
              <Form.Label>Steps</Form.Label>
              <Form.Control
                as="textarea"
                name="steps"
                value={formData.steps.map((step) => JSON.stringify(step))}
                onChange={this.handleChange}
              />
            </Form.Group>

            <Button variant="primary" type="submit">
              Save
            </Button>
          </Form>
        ) : (
          <div>
            {/* Existing display fields */}
            {/* ... */}

            {/* New display fields */}
            <p>
              <strong>Category:</strong> {formData.category}
            </p>
            <p>
              <strong>Ingredients:</strong> {formData.ingredients.map((ingredient) => JSON.stringify(ingredient)).join(', ')}
            </p>
            <p>
              <strong>Tags:</strong> {formData.tags.join(', ')}
            </p>
            <p>
              <strong>Steps:</strong> {formData.steps.map((step) => JSON.stringify(step)).join(', ')}
            </p>
          </div>
        )}

        {!isEditing && (
          <Button variant="secondary" onClick={this.handleEdit}>
            Edit
          </Button>
        )}
      </div>
    );
  }
}

export default RecipeDisplay;
