import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Row, Col, Button } from 'react-bootstrap';
import '../styles/HomePage.css';

function HomePage() {

    const [buttonsEnabled, setbuttonsEnabled] = useState(true);
    const navigate = useNavigate();

    const handleCreateRecipe = () => {
        navigate('/create-recipe');
    };

    const handleBrowseRecipes = () => {
    
    };

    const handleMealPlanner = () => {
    
    };

    const handleGroceryList = () => {
    
    };

    const getServerStatus = async () => {
        try {
          const response = await fetch('http://localhost:8080/recipes/status/');
          const status = response.status;
          return status === 202;
        } catch (error) {
          console.error(error);
          return false;
        }
      };

      //For checking server status
      useEffect(() => {
        const intervalId = setInterval(() => {
          if (!getServerStatus() & buttonsEnabled) 
            disableButtons();
          else if (getServerStatus() & !buttonsEnabled) 
            enableButtons();
        }, 60000);
    
        // Clear the interval when the component unmounts
        return () => clearInterval(intervalId);
      }, []);

      const enableButtons = () => {
        buttonsEnabled = true;
      }

      const disableButtons = () => {
        buttonsEnabled = false;
      }
      

  return (
    <div className="home-page">
      <h1 className="title">Digital Kitchen</h1>
      <Container className="button-container">
        <Row>
          <Col>
            <Button variant="primary" size="lg" 
            className="oval-button create-recipe-button" onClick={handleCreateRecipe}
            >Create a Recipe
            </Button>
          </Col>
        </Row>
        <Row>
          <Col>
            <Button variant="primary" size="lg" 
            className="oval-button browse-recipes-button" onClick={handleBrowseRecipes}
            >Browse Recipes
            </Button>
          </Col>
        </Row>
        <Row>
          <Col>
            <Button variant="primary" size="lg" 
            className="oval-button meal-planner-button" onClick={handleMealPlanner}
            >Meal Planner
            </Button>
          </Col>
        </Row>
        <Row>
          <Col>
            <Button variant="primary" size="lg" 
            className="oval-button grocery-list-button" onClick={handleGroceryList}
            >Grocery List
            </Button>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default HomePage;
