import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RecipeForm = ({ onNextStep, formData, setFormData }) => {
  const [recipeData, setRecipeData] = useState({
    name: formData.recipe?.name || '',
    author: formData.recipe?.author || '',
    description: formData.recipe?.description || '',
    servings: formData.recipe?.servings || '',
    caloriesPerServing: formData.recipe?.caloriesPerServing || '',
    notes: formData.recipe?.notes || '',
    category: formData.recipe?.category || '',
  });

  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/digitalkitchen/form/categories')
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Error retrieving categories');
        }
      })
      .then((data) => {
        setCategories(data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    const updatedFormData = {
      ...formData,
      recipe: recipeData,
    };
    setFormData(updatedFormData);
    onNextStep(); // Navigate to the next step
  };

  const handleRecipeChange = (event) => {
    const { name, value } = event.target;
    setRecipeData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input
          type="text"
          name="name"
          value={recipeData.name}
          onChange={handleRecipeChange}
        />
      </label>
      <label>
        Author:
        <input
          type="text"
          name="author"
          value={recipeData.author}
          onChange={handleRecipeChange}
        />
      </label>
      <label>
        Description:
        <textarea
          rows={3}
          name="description"
          value={recipeData.description}
          onChange={handleRecipeChange}
        />
      </label>
      <label>
        Servings:
        <input
          type="number"
          name="servings"
          value={recipeData.servings}
          onChange={handleRecipeChange}
        />
      </label>
      <label>
        Calories per serving:
        <input
          type="number"
          name="caloriesPerServing"
          value={recipeData.caloriesPerServing}
          onChange={handleRecipeChange}
        />
      </label>
      <label>
        Notes:
        <textarea
          name="notes"
          value={recipeData.notes}
          onChange={handleRecipeChange}
        />
      </label>
      <label>
        Category:
        <select
          name="category"
          value={recipeData.category}
          onChange={handleRecipeChange}
        >
          <option value="">--Select a category--</option>
          {categories.map((category) => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </select>
      </label>
      <button type="submit">Next</button>
    </form>
  );
};

export default RecipeForm;
